package Hotel.Management.System;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class admin extends JFrame implements ActionListener {    JButton add_Employee, add_Room, add_Drivers, logout, back;    admin(){        setLayout(null);        setSize(1280,720);        setLocation(0,0);        add_Employee = new JButton("ADD EMPLOYEE");        add_Employee.setBounds(250, 150, 160, 25);        add_Employee.setFont(new Font("Tahoma", Font.BOLD, 13));        add_Employee.addActionListener(this);        add(add_Employee);        add_Room = new JButton("ADD ROOM");        add_Room.setBounds(250, 250, 160, 25);        add_Room.setFont(new Font("Tahoma", Font.BOLD, 13));        add_Room.addActionListener(this);        add(add_Room);        add_Drivers = new JButton("ADD DRIVERS");        add_Drivers.setBounds(250, 350, 160, 25);        add_Drivers.setFont(new Font("Tahoma", Font.BOLD, 13));        add_Drivers.addActionListener(this);        add(add_Drivers);        logout = new JButton("Logout");        logout.setBounds(90, 450, 90, 25);        logout.setFont(new Font("Tahoma", Font.BOLD, 12));        logout.addActionListener(this);        add(logout);        back = new JButton("Back");        back.setBounds(300, 450, 90, 25);        back.setFont(new Font("Tahoma", Font.BOLD, 12));        back.addActionListener(this);        add(back);        ImageIcon empIcon = new ImageIcon(ClassLoader.getSystemResource("icon/employees.png"));        Image empImg = empIcon.getImage().getScaledInstance(80, 80, Image.SCALE_DEFAULT);        JLabel empLabel = new JLabel(new ImageIcon(empImg));        empLabel.setBounds(80, 120, 80, 80);        add(empLabel);        ImageIcon roomIcon = new ImageIcon(ClassLoader.getSystemResource("icon/room.png"));        Image roomImg = roomIcon.getImage().getScaledInstance(80, 80, Image.SCALE_DEFAULT);        JLabel roomLabel = new JLabel(new ImageIcon(roomImg));        roomLabel.setBounds(80, 220, 80, 80);        add(roomLabel);        ImageIcon driverIcon = new ImageIcon(ClassLoader.getSystemResource("icon/driver.png"));        Image driverImg = driverIcon.getImage().getScaledInstance(80, 80, Image.SCALE_DEFAULT);        JLabel driverLabel = new JLabel(new ImageIcon(driverImg));        driverLabel.setBounds(80, 320, 80, 80);        add(driverLabel);        ImageIcon gifIcon = new ImageIcon(ClassLoader.getSystemResource("icon/rahadall1.gif"));        Image gifImg = gifIcon.getImage().getScaledInstance(600, 600, Image.SCALE_DEFAULT);        JLabel gifLabel = new JLabel(new ImageIcon(gifImg));        gifLabel.setBounds(650, 100, 600, 600);        add(gifLabel);        ImageIcon bgGif = new ImageIcon(ClassLoader.getSystemResource("icon/adminbc.gif"));        JLabel bgLabel = new JLabel(bgGif);        bgLabel.setBounds(0, 0, 1280, 720);        add(bgLabel);        setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == add_Employee){            new AddEmployee();        } else if (e.getSource() == add_Room) {            new AddRoom();        } else if (e.getSource() ==  add_Drivers) {            new addDriver();        } else if (e.getSource() == logout){            System.exit(102);        } else if (e.getSource() == back) {            new Dashboard();            setVisible(false);        }    }    public static void main(String[] args) {        new admin();    }}